class Legend {
    
    enum `Type`: String {
        case gk = "gk"
        case df = "df"
        case mf = "mf"
        case fw = "fw"
    }
    
    var name: String
    var type: Type
    var shortDescription: String
    var longDescription: String
    var image = UIImage(named:"")
    
    init(name: String, type: Type, shortDescription: String, longDescription: String, playerImage: String) {
        self.name = name
        self.type = type
        self.shortDescription = shortDescription
        self.longDescription = longDescription
        self.image = UIImage(named: playerImage)
    }
}


